contracts/BridgeIN.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import {IHandler} from "./interfaces/IHandler.sol";
import "hardhat/console.sol"; // Import Hardhat console library


contract BridgeIN {
    address owner;
    uint depositNonce;
    address erc20Handler;

    modifier onlyOwner() {
        _onlyOwner();
        _;
    }

    function _onlyOwner() private view {
        require(msg.sender == owner, "sender must be owner");
    }

    event Deposit(
        address tokenAddress,
        address depositor,
        uint amount,
        uint nonce
    );

    function deposit(
        address tokenAddress,
        address depositor,
        uint256 amount
    ) external {

        depositNonce++;

        IHandler depositHandler = IHandler(erc20Handler);
        (address returnedTokenAddress, address returnedDepositor, uint256 returnedAmount) = depositHandler.deposit(tokenAddress, depositor, amount);

        emit Deposit(returnedTokenAddress, returnedDepositor, returnedAmount, depositNonce);
    }

    function handleWithdraw(address token, uint amount, uint64  withdrawNonce, address user) external returns(address _user) {
        console.log("================ Got new Withdraw| User:", user);
        return user;
    }

    function setHandler(address _handler) external onlyOwner {
        erc20Handler = _handler;
    }
}

